def mat_dist_4(m1,m2):
	a = array([array(meshgrid(c1,c2)) for c1,c2 in zip(array(m1.T),array(m2.T))])
	return npsum(npmin(npsum(square(a[:,1]-a[:,0]),axis = 0),axis = 0))

